svg.script:
var R=0
var G=0
var B=0

  function setColorR(v){
  var re=document.getElementById("rect")
  R=Math.floor(255*v/360)
  re.setAttribute("fill","rgb("+R+","+G+","+B+")")
  document.getElementById("txR").firstChild.nodeValue=R
  }
  function setColorG(v){
  var re=document.getElementById("rect")
  G=Math.floor(255*v/360)
  re.setAttribute("fill","rgb("+R+","+G+","+B+")")
  document.getElementById("txG").firstChild.nodeValue=G
  }
  function setColorB(v){
  var re=document.getElementById("rect")
  B=Math.floor(255*v/360)
  re.setAttribute("fill","rgb("+R+","+G+","+B+")")
  document.getElementById("txB").firstChild.nodeValue=B
  }
    function setColorA(v){
  var re=document.getElementById("rect")

document.getElementById("txA").firstChild.nodeValue=Math.floor(1000*v/360)/1000

  re.setAttribute("opacity",v/360)
  } 
  function init(){
  var kno= new knob(100,850,80,60,55,80,-5,0,0,15,15,setColorR)
 document.documentElement.appendChild(kno.svg)

  var kno= new knob(350,850,90,65,55,65,-2,0,0,5,25,setColorG)
 document.documentElement.appendChild(kno.svg)

  var kno= new knob(600,850,90,80,50,45,-2,0,0,0,15,setColorB)
 document.documentElement.appendChild(kno.svg)

   var kno= new knob(850,850,90,70,45,35,-2,0,0,10,15,setColorA)
 document.documentElement.appendChild(kno.svg)
  }
